using Xunit;

namespace StateSmith.CliTest.Setup;

public class VscodeLaunchJsonModderTest
{
    [Fact]
    public void TestAddHedietVscodePlugin()
    {
        // this is generated by `dotnet-script init` version 1.4.0
        var v_1_4_0 = """
                        {
                "version": "0.2.0",
                "configurations": [
                    {
                        "name": ".NET Script Debug",
                        "type": "coreclr",
                        "request": "launch",
                        "program": "dotnet",
                        "args": [
                            "exec",
                            "C:/Users/user/.dotnet/tools/.store/dotnet-script/1.4.0/dotnet-script/1.4.0/tools/net6.0/any/dotnet-script.dll",
                            "${file}"
                        ],
                        "cwd": "${workspaceRoot}",
                        "stopAtEntry": false
                    }
                ]
            }
            """;

        // this is generated by `dotnet-script init` version 1.5.0
        var v1_5_0 = """
                        {
              "version": "0.2.0",
              "configurations": [
                {
                  "name": ".NET Script Debug",
                  "type": "coreclr",
                  "request": "launch",
                  "program": "${env:HOME}/.dotnet/tools/dotnet-script",
                  "args": ["${file}"],
                  "windows": {
                    "program": "${env:USERPROFILE}/.dotnet/tools/dotnet-script.exe",
                  },
                  "cwd": "${workspaceFolder}",
                  "stopAtEntry": false,
                }
              ]
            }
            """;

        // recommend not commiting omnisharp.json file as it is user specific 
        var aGeneratedOminsharpJsonFile = """
            {
              "script": {
                "enableScriptNuGetReferences": true,
                "defaultTargetFramework": "net6.0"
              }
            }
            """;
    }
}
